DELIMITER //
CREATE PROCEDURE sp_crearIdioma(
	IN  id_usuarioCreador INT,
	IN  codigo            VARCHAR(3),
	IN  idioma            VARCHAR(50),
	OUT resultado         VARCHAR(500)
)
bye:
BEGIN
	DECLARE id_idioma, id, fin INT DEFAULT -1;
	
	DECLARE lang_cursor_1 CURSOR FOR SELECT id_idioma FROM tb_idioma;
	DECLARE lang_cursor_2 CURSOR FOR SELECT id_idioma FROM tb_idioma_t WHERE id_idioma = 1;

	DECLARE CONTINUE HANDLER FOR NOT FOUND SET fin = 1;

	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		SET resultado = 'ERROR - 0 - Ocurrio una excepcion';
		SELECT 0 AS res;
		ROLLBACK;
	END;
	
	IF id_usuarioCreador IS NOT NULL AND NOT EXISTS (SELECT * FROM tb_usuario AS U WHERE U.id_usuario = id_usuarioCreador) THEN
		SET resultado = 'ERROR - 1 - Usuario creador no existe';
		SELECT -1 AS res;
		LEAVE bye;
	END IF;

	START TRANSACTION;
		INSERT INTO tb_idioma (codigo, id_usuarioCreador)
		VALUES (codigo, id_usuarioCreador);

		SET id = LAST_INSERT_ID();

		IF id > 0 THEN
			OPEN lang_cursor_1;
			get_lang_1: LOOP
				FETCH lang_cursor_1 INTO id;
				IF fin = 1 THEN
					LEAVE get_lang_1;
				END IF;
			
				IF id <> id_idioma THEN
					INSERT INTO tb_idioma_t (idioma, id_idioma, id_usuarioCreador)
					VALUES (idioma, id, id_usuarioCreador);
				END IF;
			END LOOP get_lang_1;

			CLOSE lang_cursor_1;
			
			OPEN lang_cursor_2;
			get_lang_2: LOOP
				FETCH lang_cursor_2 INTO id;
				IF fin = 1 THENe
					LEAVE get_lang_2;
				END IF;
			
				INSERT INTO tb_idioma_t (idioma, id_idioma, id_usuarioCreador)
				VALUES (idioma, id_idioma, id_usuarioCreador);
			END LOOP get_lang_2;

			CLOSE lang_cursor_2;

			SET resultado = 'OK - 1 - Exito operacion';

			SELECT id_idioma AS res;
		ELSE
			SET resultado = 'ERROR - 2 - Error creando registro';
			SELECT -2 AS res;
			LEAVE bye;
		END IF;
	COMMIT;
	
END;
//
DELIMITER ;

