	IF NOT EXISTS (SELECT * FROM tb_idioma AS I WHERE I.id_idioma = id_idioma) THEN
		SET resultado = 'ERROR - 2 - Idioma no existe';
		SELECT -2 AS res;
		LEAVE bye;
	END IF;

