				SET I.codigo = codigo, I.activo = activo, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma;
			ELSEIF codigo IS NOT NULL THEN
				UPDATE tb_idioma AS I
				SET I.codigo = codigo, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma;
			ELSEIF activo IS NOT NULL THEN
				UPDATE tb_idioma AS I
				SET I.activo = activo, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma;
			ELSE
				SET flag = 0;
			END IF;
		ELSE
			SET flag = 0;
		END IF;

		IF idioma IS NOT NULL OR activo IS NOT NULL THEN
			SET flag = 1;

			IF idioma IS NOT NULL AND activo IS NOT NULL THEN
				UPDATE tb_idioma_t AS I
				SET I.idioma = idioma, I.activo = activo, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma AND I.id_idioma_t = id_idioma_t;
			ELSEIF idioma IS NOT NULL THEN
				UPDATE tb_idioma_t AS I
				SET I.idioma = idioma, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma AND I.id_idioma_t = id_idioma_t;
			ELSEIF activo IS NOT NULL THEN
				UPDATE tb_idioma_t AS I
				SET I.activo = activo, I.id_usuarioModificador = id_usuarioModificador
				WHERE I.id_idioma = id_idioma AND I.id_idioma_t = id_idioma_t;
			ELSE
				SET flag = 0;
			END IF;
		END IF;
	COMMIT;

	IF flag = 1 THEN
		SET resultado = 'OK - 1 - Exito operacion';
	ELSE
		SET resultado = 'OK - 2 - No hubo cambios';
	END IF;

	SELECT id_idioma AS res;
END;
//
