		SET resultado = 'ERROR - 4 - Ciudad no existe';
		SELECT -4 AS res;
		LEAVE bye;
	END IF;

	START TRANSACTION;
		IF id_usuarioCreador IS NOT NULL THEN
			IF id_superDistribuidor IS NOT NULL THEN
				INSERT INTO tb_distribuidor (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_superDistribuidor, id_usuarioCreador)
				VALUES (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_superDistribuidor, id_usuarioCreador);
