				INSERT INTO tb_distribuidor (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_usuarioCreador)
				VALUES (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_usuarioCreador);
			END IF;
		ELSE
			IF id_superDistribuidor IS NOT NULL THEN
				INSERT INTO tb_distribuidor (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_superDistribuidor)
				VALUES (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad, id_superDistribuidor);
