				VALUES (identificacion, nombre, razonSocial, telefono, fax, correo, direccion, id_ciudad);
			END IF;
		END IF;

		SET id_distribuidor = LAST_INSERT_ID();

		IF id_distribuidor > 0 THEN
			SET resultado = 'OK - 1 - Exito operacion';
			SELECT id_distribuidor AS res;
		ELSE
			SET resultado = 'ERROR - 5 - Error creando registro';
			SELECT -5 AS res;
			LEAVE bye;
		END IF;
	COMMIT;
END;
