CREATE PROCEDURE sp_crearDispositivo(
	IN  id_usuarioCreador  INT,
	IN  identificacion     VARCHAR(25),
	IN  nombre             VARCHAR(20),
	IN  marca              VARCHAR(50),
	IN  modelo             VARCHAR(50),
	IN  serial             VARCHAR(50),
	IN  sistemaOperativo   VARCHAR(50),
	IN  version            VARCHAR(10),
	IN  id_tipoDispositivo INT,
	OUT resultado          VARCHAR(500)
)
bye:
BEGIN
	DECLARE id_dispositivo INT DEFAULT -1;
	
	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		SET resultado = 'ERROR - 0 - Ocurrio una excepcion';
		SELECT 0 AS res;
		ROLLBACK;
	END;

	IF id_usuarioCreador IS NOT NULL AND NOT EXISTS (SELECT * FROM tb_usuario AS U WHERE U.id_usuario = id_usuarioCreador) THEN
		SET resultado = 'ERROR - 1 - Usuario creador no existe';
		SELECT -1 AS res;
		LEAVE bye;
	END IF;

	IF NOT EXISTS (SELECT * FROM tb_tipodispositivo AS TD WHERE TD.id_tipoDispositivo = id_tipoDispositivo) THEN
		SET resultado = 'ERROR - 2 - Tipo dispositivo no existe';
		SELECT -2 AS res;
		LEAVE bye;
	END IF;
	
	IF identificacion IS NULL OR nombre IS NULL OR marca IS NULL OR modelo IS NULL OR serial IS NULL THEN
		SET resultado = 'ERROR - 3 - Identificacion, nombre, marca, modelo o serial invalido';
		SELECT -3 AS res;
		LEAVE bye;
	END IF;

	START TRANSACTION;
		IF id_usuarioCreador IS NOT NULL THEN
			INSERT INTO tb_dispositivo (identificacion, nombre, marca, modelo, serial, sistemaOperativo, version, id_usuarioCreador)
			VALUES (identificacion, nombre, marca, modelo, serial, sistemaOperativo, version, id_usuarioCreador);
		ELSE
			INSERT INTO tb_dispositivo (identificacion, nombre, marca, modelo, serial, sistemaOperativo, version)
			VALUES (identificacion, nombre, marca, modelo, serial, sistemaOperativo, version);
		END IF;

		SET id_dispositivo = LAST_INSERT_ID();

		IF id_dispositivo > 0 THEN
			SET resultado = 'OK - 1 - Exito operacion';
			SELECT id_dispositivo AS res;
		ELSE
			SET resultado = 'ERROR - 4 - Error creando registro';
			SELECT -4 AS res;
			LEAVE bye;
		END IF;
	COMMIT;
END;
