DELIMITER //
CREATE PROCEDURE sp_validarRespuesta(
	IN  id_usuarioModificador INT,
    IN  id_pregunta           INT,
	IN  id_respuesta          INT,
	IN  id_consumidor         INT,
	OUT resultado             VARCHAR(500)
)
bye:
BEGIN
	DECLARE id_tipoPregunta, correcta INT         DEFAULT 0;
	DECLARE respuesta                 VARCHAR(50) DEFAULT '';

	DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		SET resultado = 'ERROR - 0 - Ocurrio una excepcion';
		SELECT 0 AS res;
        ROLLBACK;
	END;

	IF id_usuarioModificador IS NOT NULL AND NOT EXISTS (SELECT * FROM tb_usuario AS U WHERE U.id_usuario = id_usuarioModificador) THEN
		SET resultado = 'ERROR - 1 - Usuario modificador no existe';
		SELECT -1 AS res;
        LEAVE bye;
	END IF;

	IF NOT EXISTS (SELECT * FROM tb_pregunta AS P WHERE P.id_pregunta = id_pregunta) THEN
		SET resultado = 'ERROR - 2 - Pregunta no existe';
		SELECT -2 AS res;
        LEAVE bye;
	END IF;

	IF NOT EXISTS (SELECT * FROM tb_respuesta AS R WHERE R.id_respuesta = id_respuesta) THEN
		SET resultado = 'ERROR - 3 - Respuesta no existe';
		SELECT -3 AS res;
        LEAVE bye;
	END IF;

	IF NOT EXISTS (SELECT * FROM tb_consumidor AS C WHERE C.id_consumidor = id_consumidor) THEN
		SET resultado = 'ERROR - 4 - Consumidor no existe';
		SELECT -4 AS res;
        LEAVE bye;
	END IF;

	SELECT
		IFNULL(R.respuesta, '') INTO respuesta
	FROM
		tb_respuesta AS R
	WHERE
		R.id_pregunta = id_pregunta AND
		R.id_respuesta = id_respuesta AND
		R.id_consumidor = id_consumidor;

	IF respuesta <> '' THEN
		SELECT
			IFNULL(P.id_tipoPregunta, 0) INTO id_tipoPregunta
		FROM
			tb_pregunta AS P
		WHERE
			R.id_pregunta = id_pregunta;

		IF id_tipoPregunta IN (1, 2, 3) THEN
			SELECT
				IFNULL(R.id_respuestaOpcion, 0) INTO correcta
			FROM
				tb_respuestaopcion AS R
			WHERE
				R.respuestaOpcion LIKE CONCAT('%', respuesta , '%') AND
				R.id_pregunta = id_pregunta AND
				R.correcta = 1;

			IF correcta > 0 THEN
				START TRANSACTION;
					UPDATE tb_respuesta	AS R
					SET correcta = 1
					WHERE R.id_respuesta = id_respuesta;
				COMMIT;
			END IF;
		ELSE
			SET resultado = 'ERROR - 5 - Tipo de pregunta no compatible';
			SELECT -5 AS res;
            LEAVE bye;
		END IF;
	END IF;

	IF correcta > 0 THEN
		SET resultado = 'Ok - 1 - Respuesta correcta';
		SELECT 100 AS res;
	ELSE
		SET resultado = 'Ok - 2 - Respuesta incorrecta';
		SELECT -100 AS res;
	END IF;
END;
