            INNER JOIN
            tb_usuariod_empresa AS UDE
            ON UD.id_usuarioD = UDE.id_usuarioD
            INNER JOIN
            tb_empresa AS E
            ON UDE.id_empresa = E.id_empresa
		WHERE
