)
bye:
BEGIN
	DECLARE id_sucursal, id_empresa, id_tipoNodo, id_objeto, id_tipoNodoPadre, id_objetoPadre INT DEFAULT 0;
	
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		SET resultado = 'ERROR - 0 - Ocurrio una excepcion';
