	WHERE
		D.id_dispositivo = id_dispositivo AND D.activo = 1;

	IF id_sucursal <= 0 THEN
		SET resultado = 'ERROR - 5 - Sucursal no existe';
		SELECT -5 AS res;
        LEAVE bye;
	END IF;

	SELECT
		IFNULL(S.id_empresa, 0) INTO id_empresa
	FROM
		tb_sucursal AS S
	WHERE
		S.id_sucursal = id_sucursal AND S.activo = 1;

	IF id_empresa <= 0 THEN
		SET resultado = 'ERROR - 6 - Empresa no existe';
		SELECT -6 AS res;
        LEAVE bye;
	END IF;

	SELECT
		IFNULL(N.id_tipoNodo, 0), IFNULL(N.id_objeto, 0), IFNULL(N.id_tipoNodoPadre, 0), IFNULL(N.id_objetoPadre, 0) INTO id_tipoNodo, id_objeto, id_tipoNodoPadre, id_objetoPadre
	FROM
