	IF id_tipoNodoPadre <= 0 THEN
		SET resultado = 'ERROR - 9 - Tipo nodo padre no existe';
		SELECT -9 AS res;
        LEAVE bye;
	END IF;
	
	IF id_objetoPadre <= 0 THEN
		SET resultado = 'ERROR - 10 - Objeto padre no existe';
		SELECT -10 AS res;
        LEAVE bye;
	END IF;

	IF id_tipoNodo <> 2 THEN
		START TRANSACTION;
			IF NOT EXISTS (SELECT * FROM tb_consumidor_nodo AS CN WHERE CN.id_consumidor = id_consumidor AND CN.id_nodo = id_nodo) THEN
				INSERT INTO tb_consumidor_nodo (id_consumidor, id_nodo)
				VALUES (id_consumidor, id_nodo);
