				SET
					MSG.contador = MSG.contador + 1
				WHERE
					N.id_nodo = id_nodo AND N.id_tipoNodo = id_tipoNodo;
			ELSEIF id_tipoNodo = 5 /*'FORM'*/ THEN
				UPDATE 
					tb_formularioconsumidor AS FORM
					INNER JOIN
					tb_nodo AS N
					ON FORM.id_formularioConsumidor = N.id_objeto
				SET
					FORM.contador = FORM.contador + 1
				WHERE
					N.id_nodo = id_nodo AND N.id_tipoNodo = id_tipoNodo;
			ELSEIF id_tipoNodo = 6 /*'SCRN'*/ THEN
				UPDATE 
					tb_mensajepantalla AS SCRN
					INNER JOIN
					tb_nodo AS N
					ON SCRN.id_mensajePantalla = N.id_objeto
				SET
					SCRN.contador = SCRN.contador + 1
				WHERE
					N.id_nodo = id_nodo AND N.id_tipoNodo = id_tipoNodo;
			ELSEIF id_tipoNodo = 7 /*'INV'*/ THEN
				UPDATE 
					tb_invitacion AS INV
					INNER JOIN
					tb_nodo AS N
					ON INV.id_invitacion = N.id_objeto
				SET
					INV.contador = INV.contador + 1
				WHERE
